import { Route, Routes } from "react-router-dom";
import "./App.css";
import Navbar from "./component/Navbar";
import Products from "./component/Products";
import About from "./pages/About";
import MainSection from "./pages/MainSection";

function App() {
  // console.log("Product Data", productData);
   
  const[isMenuOpen , setIsMenuOpen] = useState(false);


  // function to control the humburger menu using usestate hook
  function humMenu(){

    setIsMenuOpen(!isMenuOpen);

  }


  // function for scrolling to the particulat part 

  const scrollToSection = (id) =>  {

    const element = document.getElementById(id);

    if(element){
      element.scrollIntoView({behavior:'smooth'});
    }

  };


  //function to handle menu and scroll both at same time 

  const handleMenuandScroll = (id) => {
    humMenu()
    scrollToSection(id)
    
    
  }




  return (
    <div className="App overflow-x-hidden">
      <Navbar isMenuOpen={isMenuOpen}  setIsMenuOpen={setIsMenuOpen} humMenu={humMenu} scrollToSection={scrollToSection}
       handleMenuandScroll={handleMenuandScroll}></Navbar>
      <MainSection scrollToSection={scrollToSection} />
      <Products  />
      <About/>
      
     
      
     

      {/* <Routes>
        <Route path="/" element={ <MainSection />}/>
        <Route path='/products' element={<Products />}/>
      </Routes> */}


      
    </div>
  );
}

export default App;
